const showDate = (yyyy,mm,dd) => {
  const  moondate = document.querySelector('.js-moondate');
    let month = '';

if(mm == 01){
    month = 'January'
} else if(mm == 02){
    month = 'February'
} else if(mm == 03){
    month = 'March'
} else if(mm == 04){
    month = 'April'
} else if(mm == 05){
    month = 'May'
} else if(mm == 06){
    month = 'June'
} else if(mm == 07){
    month = 'July'
} else if(mm == 08){
    month = 'August'
} else if(mm == 09){
    month = 'September'
} else if(mm == 10){
    month = 'October'
} else if(mm == 11){
    month = 'November'
} else if(mm == 12){
    month = 'December'
};


  moondate.innerHTML = month + ' ' + dd + ' - ' + yyyy;
};


const nameMoon = (moonphase) => {
    const prefix = document.querySelector('.js-prefix'),
    moonname = document.querySelector('.js-moonname')

    if(moonphase == 'New Moon'){
        prefix.innerHTML = `It's`;
        moonname.innerHTML = `on vacay`;
        moonname.classList.add('u-briefcase');
        
        
        } else if(moonphase == 'Waxing Crescent' || moonphase == 'Waning Crescent'){
            prefix.innerHTML = `A`;
            moonname.innerHTML = `calzone`;
            moonname.classList.add('u-calzone');
        
        } else if(moonphase == 'First Quarter' || moonphase == 'Last Quarter'){
            prefix.innerHTML = `A`;
            moonname.innerHTML = `slice O' melon`;
            moonname.classList.add('u-melon');
        
        } else if(moonphase == 'Waxing Gibbous' || moonphase == 'Waning Gibbous') {
            prefix.innerHTML = `A`;
            moonname.innerHTML = `bright smile`;
            moonname.classList.add('u-smile');
        
        } else if(moonphase == 'Full Moon') {
            prefix.innerHTML = `A`;
            moonname.innerHTML = `cookie`;
            moonname.classList.add('u-cookie');
        
        } else {
            console.log("Error");
        };

};


